```svelte
<!-- lib/components/UserCard.svelte -->
<script lang="ts">
  export let user: {
    id: number;
    name: string;
    email: string;
    avatar?: string;
  };

  export let showEmail = true;
  export let clickable = false;

  import { createEventDispatcher } from 'svelte';

  const dispatch = createEventDispatcher<{
    click: { user: typeof user };
  }>();

  function handleClick() {
    if (clickable) {
      dispatch('click', { user });
    }
