### Load Function
```ts
// routes/users/+page.ts
import type { PageLoad } from './$types';

export const load: PageLoad = async ({ fetch }) => {
  const response = await fetch('/api/users');

  if (!response.ok) {
    throw new Error('Failed to load users');
  }

  const users = await response.json();

  return {
    users
  };
